
public class WeightArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] weight = {79, 13, 64, 52, 67, 98, 87, 71, 79, 101};
		
		System.out.println("The Weight of the fifth person: " +weight[4]);
		
		weight[3] = 200;
		
		System.out.println("The Weight of the third person: "+weight[3]);
		
		weight[8] = 120;
		
		System.out.println("The Weight of the eighth person: "+weight[8]);
		
		System.out.println("Length of the weight array: "+weight.length); 
		
		

	}

}
